//LengthOfStringTRYYYYYY without loop,tochararray,length----------

public class LengthOfStringTRYYYYYY {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str="iiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiii";
		int i=0;
		int len=0;
		for(i=0;str.charAt(i)!='\0';i++)
		{
			len++;
		}
		System.out.println(len);

	}

}
